  --bg-black-700: var(--_gray-300);
  --bg-black-800: var(--_gray-200);
  --bg-black-900: var(--_white);
  --bg-black-950: var(--_white);
  --bg-black-disabled: var(--_white) / 0.4;

  /* Background - Gray */
  --bg-gray-50: var(--_gray-930);

  /* bg - Primary */
  --bg-primary-50: var(--_primary-950);
  --bg-primary-100: var(--_primary-900);
  --bg-primary-200: var(--_primary-800);
  --bg-primary-300: var(--_primary-700);
  --bg-primary-400: var(--_primary-600);
  --bg-primary-500: var(--_primary-500);
  --bg-primary-600: var(--_primary-400);
  --bg-primary-700: var(--_primary-300);
  --bg-primary-800: var(--_primary-200);
  --bg-primary-900: var(--_primary-100);
  --bg-primary-950: var(--_primary-50);
  --bg-primary-disabled: var(--_primary-950);

  /* bg - Danger */
  --bg-danger-50: var(--_danger-950);
  --bg-danger-100: var(--_danger-900);
  --bg-danger-200: var(--_danger-800);
  --bg-danger-300: var(--_danger-700);
  --bg-danger-400: var(--_danger-600);
  --bg-danger-500: var(--_danger-500);
  --bg-danger-600: var(--_danger-400);
  --bg-danger-700: var(--_danger-300);
  --bg-danger-800: var(--_danger-200);
  --bg-danger-900: var(--_danger-100);
  --bg-danger-950: var(--_danger-50);
  --bg-danger-disabled: var(--_danger-950);

  /* bg - Success */
  --bg-success-50: var(--_success-950);
  --bg-success-100: var(--_success-900);
  --bg-success-200: var(--_success-800);
  --bg-success-300: var(--_success-700);
  --bg-success-400: var(--_success-600);
  --bg-success-500: var(--_success-500);
  --bg-success-600: var(--_success-400);
  --bg-success-700: var(--_success-300);
  --bg-success-800: var(--_success-200);
  --bg-success-900: var(--_success-100);
  --bg-success-950: var(--_success-50);
  --bg-success-disabled: var(--_success-950);

  /* bg - Warning */
  --bg-warning-50: var(--_warning-950);
  --bg-warning-100: var(--_warning-900);
  --bg-warning-200: var(--_warning-800);
  --bg-warning-300: var(--_warning-700);
  --bg-warning-400: var(--_warning-600);
  --bg-warning-500: var(--_warning-500);
  --bg-warning-600: var(--_warning-400);
  --bg-warning-700: var(--_warning-300);
  --bg-warning-800: var(--_warning-200);
  --bg-warning-900: var(--_warning-100);
  --bg-warning-950: var(--_warning-50);
  --bg-warning-disabled: var(--_warning-950);

  /* Text - White */
  --txt-white: var(--_gray-900);
  --txt-white-disabled: var(--_gray-900) / 0.4;

  /* Text - Black */
  --txt-black-900: var(--_white);
  --txt-black-700: var(--_gray-300);
  --txt-black-500: var(--_gray-400);
