  /* Text - Primary */
  --txt-primary: var(--_primary-400);
  --txt-primary-disabled: var(--_primary-400) / 0.4;

  /* Text - Danger */
  --txt-danger: var(--_danger-400);
  --txt-danger-disabled: var(--_danger-400) / 0.4;

  /* Text - Success */
  --txt-success: var(--_success-500);
  --txt-success-disabled: var(--_success-500) / 0.4;

  /* Text - Warning */
  --txt-warning: var(--_warning-500);
  --txt-warning-disabled: var(--_warning-500) / 0.4;

  /* Outline - Divider */
  --otl-divider: var(--_gray-850);
