/* MYDS Theme Configuration */
/**
 * Tailwind CSS 4.0 Configuration
 * This file contains all theme customizations converted from tailwind.config.js
 */

@theme {
  /* === Spacing === */
  --spacing-2\.5: 0.625rem;
  --spacing-4\.5: 1.125rem;
  --spacing-10\.5: 2.625rem;

  /* === Font Sizes === */
  --font-size-default: 0.875rem;
  --font-size-default--line-height: 1.25rem;

  --font-size-heading-xl: 3.75rem;
  --font-size-heading-xl--line-height: 4.5rem;
  --font-size-heading-lg: 3rem;
  --font-size-heading-lg--line-height: 3.75rem;
  --font-size-heading-md: 2.25rem;
  --font-size-heading-md--line-height: 2.75rem;
  --font-size-heading-sm: 1.875rem;
  --font-size-heading-sm--line-height: 2.375rem;
  --font-size-heading-xs: 1.5rem;
  --font-size-heading-xs--line-height: 2rem;
  --font-size-heading-2xs: 1.25rem;
  --font-size-heading-2xs--line-height: 1.75rem;

  --font-size-body-xl: 1.25rem;
  --font-size-body-xl--line-height: 1.875rem;
  --font-size-body-lg: 1.125rem;
  --font-size-body-lg--line-height: 1.625rem;
  --font-size-body-md: 1rem;
  --font-size-body-md--line-height: 1.5rem;
  --font-size-body-sm: 0.875rem;
  --font-size-body-sm--line-height: 1.25rem;
  --font-size-body-xs: 0.75rem;
  --font-size-body-xs--line-height: 1.125rem;

  /* === Border Radius === */
  --radius-xs: 4px;
  --radius-sm: 6px;
  --radius: 8px;
  --radius-md: 8px;
  --radius-lg: 12px;
  --radius-xl: 14px;
  --radius-full: 9999px;

  /* === Font Families === */
  --font-family-body: "Inter", ui-sans-serif, system-ui, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol", "Noto Color Emoji";
  --font-family-heading: "Poppins", ui-sans-serif, system-ui, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol", "Noto Color Emoji";
  --font-family-mono: "Roboto Mono", ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, "Liberation Mono", "Courier New", monospace;

  /* === Colors (using CSS variables from color.css) === */
  /* White */
  --color-white: rgb(var(--_white));
  --color-white-disabled: rgba(var(--_white) / 0.4);

  /* Gray scale */
  --color-gray-50: rgba(var(--_gray-50) / <alpha-value>);
  --color-gray-100: rgba(var(--_gray-100) / <alpha-value>);
  --color-gray-200: rgba(var(--_gray-200) / <alpha-value>);
  --color-gray-300: rgba(var(--_gray-300) / <alpha-value>);
  --color-gray-400: rgba(var(--_gray-400) / <alpha-value>);
  --color-gray-500: rgba(var(--_gray-500) / <alpha-value>);
  --color-gray-600: rgba(var(--_gray-600) / <alpha-value>);
  --color-gray-700: rgba(var(--_gray-700) / <alpha-value>);
  --color-gray-800: rgba(var(--_gray-800) / <alpha-value>);
  --color-gray-850: rgba(var(--_gray-850) / <alpha-value>);
  --color-gray-900: rgba(var(--_gray-900) / <alpha-value>);
  --color-gray-930: rgba(var(--_gray-930) / <alpha-value>);
  --color-gray-950: rgba(var(--_gray-950) / <alpha-value>);

  /* Primary */
  --color-primary-50: rgba(var(--_primary-50) / <alpha-value>);
  --color-primary-100: rgba(var(--_primary-100) / <alpha-value>);
  --color-primary-200: rgba(var(--_primary-200) / <alpha-value>);
  --color-primary-300: rgba(var(--_primary-300) / <alpha-value>);
  --color-primary-400: rgba(var(--_primary-400) / <alpha-value>);
  --color-primary-500: rgba(var(--_primary-500) / <alpha-value>);
  --color-primary-600: rgba(var(--_primary-600) / <alpha-value>);
  --color-primary-700: rgba(var(--_primary-700) / <alpha-value>);
  --color-primary-800: rgba(var(--_primary-800) / <alpha-value>);
  --color-primary-900: rgba(var(--_primary-900) / <alpha-value>);
  --color-primary-950: rgba(var(--_primary-950) / <alpha-value>);

  /* Danger */
  --color-danger-50: rgba(var(--_danger-50) / <alpha-value>);
  --color-danger-100: rgba(var(--_danger-100) / <alpha-value>);
  --color-danger-200: rgba(var(--_danger-200) / <alpha-value>);
  --color-danger-300: rgba(var(--_danger-300) / <alpha-value>);
  --color-danger-400: rgba(var(--_danger-400) / <alpha-value>);
  --color-danger-500: rgba(var(--_danger-500) / <alpha-value>);
  --color-danger-600: rgba(var(--_danger-600) / <alpha-value>);
  --color-danger-700: rgba(var(--_danger-700) / <alpha-value>);
  --color-danger-800: rgba(var(--_danger-800) / <alpha-value>);
  --color-danger-900: rgba(var(--_danger-900) / <alpha-value>);
  --color-danger-950: rgba(var(--_danger-950) / <alpha-value>);

  /* Success */
  --color-success-50: rgba(var(--_success-50) / <alpha-value>);
  --color-success-100: rgba(var(--_success-100) / <alpha-value>);
  --color-success-200: rgba(var(--_success-200) / <alpha-value>);
  --color-success-300: rgba(var(--_success-300) / <alpha-value>);
  --color-success-400: rgba(var(--_success-400) / <alpha-value>);
  --color-success-500: rgba(var(--_success-500) / <alpha-value>);
  --color-success-600: rgba(var(--_success-600) / <alpha-value>);
  --color-success-700: rgba(var(--_success-700) / <alpha-value>);
  --color-success-800: rgba(var(--_success-800) / <alpha-value>);
  --color-success-900: rgba(var(--_success-900) / <alpha-value>);
  --color-success-950: rgba(var(--_success-950) / <alpha-value>);

  /* Warning */
  --color-warning-50: rgba(var(--_warning-50) / <alpha-value>);
  --color-warning-100: rgba(var(--_warning-100) / <alpha-value>);
  --color-warning-200: rgba(var(--_warning-200) / <alpha-value>);
  --color-warning-300: rgba(var(--_warning-300) / <alpha-value>);
  --color-warning-400: rgba(var(--_warning-400) / <alpha-value>);
  --color-warning-500: rgba(var(--_warning-500) / <alpha-value>);
  --color-warning-600: rgba(var(--_warning-600) / <alpha-value>);
  --color-warning-700: rgba(var(--_warning-700) / <alpha-value>);
  --color-warning-800: rgba(var(--_warning-800) / <alpha-value>);
  --color-warning-900: rgba(var(--_warning-900) / <alpha-value>);
  --color-warning-950: rgba(var(--_warning-950) / <alpha-value>);

  /* Semantic background colors */
  --color-bg-white: rgb(var(--bg-white));
  --color-bg-white-hover: rgb(var(--bg-white-hover));
  --color-bg-white-disabled: rgb(var(--bg-white-disabled));
  --color-bg-washed: rgb(var(--bg-washed));
  --color-bg-washed-active: rgb(var(--bg-washed-active));
  --color-bg-contrast: rgb(var(--bg-contrast));
  --color-bg-dialog: rgb(var(--bg-dialog));
  --color-bg-dialog-active: rgb(var(--bg-dialog-active));

  --color-bg-black-900: rgb(var(--bg-black-900));
  --color-bg-black-800: rgb(var(--bg-black-800));
  --color-bg-black-700: rgb(var(--bg-black-700));
  --color-bg-black-500: rgb(var(--bg-black-500));
  --color-bg-black-400: rgb(var(--bg-black-400));
  --color-bg-black-300: rgb(var(--bg-black-300));
  --color-bg-black-200: rgb(var(--bg-black-200));
  --color-bg-black-100: rgb(var(--bg-black-100));
  --color-bg-black-50: rgb(var(--bg-black-50));
  --color-bg-black-disabled: rgb(var(--bg-black-disabled));

  --color-bg-gray-50: rgb(var(--bg-gray-50));

  --color-bg-primary-50: rgb(var(--bg-primary-50));
  --color-bg-primary-100: rgb(var(--bg-primary-100));
  --color-bg-primary-200: rgb(var(--bg-primary-200));
  --color-bg-primary-300: rgb(var(--bg-primary-300));
  --color-bg-primary-400: rgb(var(--bg-primary-400));
  --color-bg-primary-500: rgb(var(--bg-primary-500));
  --color-bg-primary-600: rgb(var(--bg-primary-600));
  --color-bg-primary-700: rgb(var(--bg-primary-700));
  --color-bg-primary-800: rgb(var(--bg-primary-800));
  --color-bg-primary-900: rgb(var(--bg-primary-900));
  --color-bg-primary-950: rgb(var(--bg-primary-950));
  --color-bg-primary-disabled: rgb(var(--bg-primary-disabled));

  --color-bg-danger-50: rgb(var(--bg-danger-50));
  --color-bg-danger-100: rgb(var(--bg-danger-100));
  --color-bg-danger-200: rgb(var(--bg-danger-200));
  --color-bg-danger-300: rgb(var(--bg-danger-300));
  --color-bg-danger-400: rgb(var(--bg-danger-400));
  --color-bg-danger-500: rgb(var(--bg-danger-500));
  --color-bg-danger-600: rgb(var(--bg-danger-600));
  --color-bg-danger-700: rgb(var(--bg-danger-700));
  --color-bg-danger-800: rgb(var(--bg-danger-800));
  --color-bg-danger-900: rgb(var(--bg-danger-900));
  --color-bg-danger-950: rgb(var(--bg-danger-950));
  --color-bg-danger-disabled: rgb(var(--bg-danger-disabled));

  --color-bg-success-50: rgb(var(--bg-success-50));
  --color-bg-success-100: rgb(var(--bg-success-100));
  --color-bg-success-200: rgb(var(--bg-success-200));
  --color-bg-success-300: rgb(var(--bg-success-300));
  --color-bg-success-400: rgb(var(--bg-success-400));
  --color-bg-success-500: rgb(var(--bg-success-500));
  --color-bg-success-600: rgb(var(--bg-success-600));
  --color-bg-success-700: rgb(var(--bg-success-700));
  --color-bg-success-800: rgb(var(--bg-success-800));
  --color-bg-success-900: rgb(var(--bg-success-900));
  --color-bg-success-950: rgb(var(--bg-success-950));
  --color-bg-success-disabled: rgb(var(--bg-success-disabled));

  --color-bg-warning-50: rgb(var(--bg-warning-50));
  --color-bg-warning-100: rgb(var(--bg-warning-100));
  --color-bg-warning-200: rgb(var(--bg-warning-200));
  --color-bg-warning-300: rgb(var(--bg-warning-300));
  --color-bg-warning-400: rgb(var(--bg-warning-400));
  --color-bg-warning-500: rgb(var(--bg-warning-500));
  --color-bg-warning-600: rgb(var(--bg-warning-600));
  --color-bg-warning-700: rgb(var(--bg-warning-700));
  --color-bg-warning-800: rgb(var(--bg-warning-800));
  --color-bg-warning-900: rgb(var(--bg-warning-900));
  --color-bg-warning-950: rgb(var(--bg-warning-950));
  --color-bg-warning-disabled: rgb(var(--bg-warning-disabled));

  /* Semantic text colors */
  --color-txt-white: rgb(var(--txt-white));
  --color-txt-white-disabled: rgb(var(--txt-white-disabled));
  --color-txt-black-900: rgb(var(--txt-black-900));
  --color-txt-black-700: rgb(var(--txt-black-700));
  --color-txt-black-500: rgb(var(--txt-black-500));
  --color-txt-black-disabled: rgb(var(--txt-black-disabled));
  --color-txt-primary: rgb(var(--txt-primary));
  --color-txt-primary-disabled: rgb(var(--txt-primary-disabled));
  --color-txt-danger: rgb(var(--txt-danger));
  --color-txt-danger-disabled: rgb(var(--txt-danger-disabled));
  --color-txt-success: rgb(var(--txt-success));
  --color-txt-success-disabled: rgb(var(--txt-success-disabled));
  --color-txt-warning: rgb(var(--txt-warning));
  --color-txt-warning-disabled: rgb(var(--txt-warning-disabled));

  /* Semantic outline colors */
  --color-otl-divider: rgb(var(--otl-divider));
  --color-otl-gray-200: rgb(var(--otl-gray-200));
  --color-otl-gray-300: rgb(var(--otl-gray-300));
  --color-otl-primary-disabled: rgb(var(--otl-primary-disabled));
  --color-otl-primary-200: rgb(var(--otl-primary-200));
  --color-otl-primary-300: rgb(var(--otl-primary-300));
  --color-otl-danger-disabled: rgb(var(--otl-danger-disabled));
  --color-otl-danger-200: rgb(var(--otl-danger-200));
  --color-otl-danger-300: rgb(var(--otl-danger-300));
  --color-otl-success-disabled: rgb(var(--otl-success-disabled));
  --color-otl-success-200: rgb(var(--otl-success-200));
  --color-otl-success-300: rgb(var(--otl-success-300));
  --color-otl-warning-disabled: rgb(var(--otl-warning-disabled));
  --color-otl-warning-200: rgb(var(--otl-warning-200));
  --color-otl-warning-300: rgb(var(--otl-warning-300));

  /* Focus ring colors */
  --color-fr-primary: rgb(var(--fr-primary));
  --color-fr-danger: rgb(var(--fr-danger));

  /* === Transition Durations === */
  --duration-200: 200ms;
  --duration-400: 400ms;
  --duration-600: 600ms;

  /* === Box Shadows === */
  --shadow-button: 0px 1px 3px 0px rgba(0, 0, 0, 0.07);
  --shadow-card: 0px 2px 6px 0px rgba(0, 0, 0, 0.05), 0px 6px 24px 0px rgba(0, 0, 0, 0.05);
  --shadow-context-menu: 0px 2px 6px 0px rgba(0, 0, 0, 0.05), 0px 12px 50px 0px rgba(0, 0, 0, 0.10);

  /* === Transform Origins === */
  --transform-origin-tooltip-origin: var(--radix-tooltip-content-transform-origin);

  /* === Keyframes === */
  @keyframes decrease-width {
    0% { width: 100%; }
    100% { width: 0%; }
  }

  @keyframes accordion-slide-down {
    from { height: 0; }
    to { height: var(--radix-accordion-content-height); }
  }

  @keyframes accordion-slide-up {
    from { height: var(--radix-accordion-content-height); }
    to { height: 0; }
  }

  @keyframes collapsible-slide-down {
    from { height: 0; }
    to { height: var(--radix-collapsible-content-height); }
  }

  @keyframes collapsible-slide-up {
    from { height: var(--radix-collapsible-content-height); }
    to { height: 0; }
  }

  @keyframes slide-up {
    from { transform: translateY(100%); }
    to { transform: translateY(0); }
  }

  @keyframes slide-down {
    from { transform: translateY(-100%); }
    to { transform: translateY(0); }
  }

  @keyframes shimmer {
    0% { background-position: 0% 0; }
    100% { background-position: 200% 0%; }
  }

  /* === Animations === */
  --animate-expire: decrease-width 5s linear;
  --animate-slide-up: slide-up 300ms ease-out;
  --animate-slide-down: slide-down 300ms ease-out;
  --animate-accordion-slide-down: accordion-slide-down 300ms ease-out;
  --animate-accordion-slide-up: accordion-slide-up 300ms ease-out;
  --animate-collapsible-slide-down: collapsible-slide-down 300ms ease-out;
  --animate-collapsible-slide-up: collapsible-slide-up 300ms ease-out;
  --animate-shimmer: shimmer 1200ms linear infinite;
}
